mapper.select:
		SELECT 
			
		FROM TB_APPLY
    	
    		1=1
    		AND COMPANYNAME like concat(concat('%',#{companyName,jdbcType=VARCHAR}),'%')
      		AND HUMANNAME like concat(concat('%',#{humanName,jdbcType=VARCHAR}),'%')
      		AND TRADEMARKNAME like concat(concat('%',#{trademarkName,jdbcType=VARCHAR}),'%')
     		AND ACCEPTTYPE =  #{acceptType,jdbcType=VARCHAR}
     		AND ACCEPTDATE >= #{beginTime}
     		AND ACCEPTDATE <= #{endTime}
		
		ORDER BY ACCEPTDATE DESC

mapper.update:
	    UPDATE TB_APPLY SET DELETEFLAG = '1' WHERE APPLYNO IN(
	    select applyNo from tb_pay_order where applyno in
	    (
            #{applyNo}
        )and status='01'
        )

mapper.select:
		SELECT 
			
		FROM TB_APPLY ,TB_APPLY_TYPE  WHERE
    	 	ACCEPTTYPE =APPLYTYPENO AND APPLYNO=#{applyNo,jdbcType=VARCHAR}
