mapper.select:
select

from tb_pay_order
where status = #{status,jdbcType=VARCHAR}
and payTime >= #{payTime,jdbcType=DATE}

mapper.update:
update tb_pay_order
set orderNo = #{orderNo,jdbcType=VARCHAR},
  payTime = #{payTime,jdbcType=DATE},
  amt = #{amt,jdbcType=DECIMAL},
  status = #{status,jdbcType=VARCHAR}
where applyNo = #{applyNo,jdbcType=VARCHAR}

mapper.select:
		SELECT 

		FROM TB_PAY_ORDER

    		1=1
    		AND applyNo = #{applyNo,jdbcType=VARCHAR}
      		AND orderNo = #{orderNo,jdbcType=VARCHAR}
     		AND STATUS =  #{status,jdbcType=VARCHAR}

		ORDER BY if(ISNULL(PAYTIME),0,1),PAYTIME,APPLYNO  DESC
