mapper.update:
UPDATE
	tb_pay_order
SET 
    payOrderNo = #{payOrderNo,jdbcType=VARCHAR}
  	payTime = #{payTime,jdbcType=TIMESTAMP},
  	payUrl = #{payUrl,jdbcType=VARCHAR},
  	amt = #{amt,jdbcType=DECIMAL},
  	status = #{status,jdbcType=VARCHAR}
WHERE 
	applyNo = #{applyNo,jdbcType=VARCHAR}

mapper.select:
	SELECT 

		FROM TB_PAY_ORDER

    		1=1
    		AND applyNo = #{applyNo,jdbcType=VARCHAR}
      		AND orderNo = #{orderNo,jdbcType=VARCHAR}
     		AND STATUS =  #{status,jdbcType=VARCHAR}

		ORDER BY if(ISNULL(PAYTIME),0,1),PAYTIME,APPLYNO  DESC

mapper.delete:
		UPDATE TB_PAY_ORDER SET DELETEFLAG = '1'  WHERE APPLYNO IN (

            #{applyNo}

        )   AND (STATUS = '01' or STATUS = '05') ;
