mapper.select:
select

from tb_pay_order
where status = #{status,jdbcType=VARCHAR}
and payTime >= #{payTime,jdbcType=TIMESTAMP}

		SELECT 

		FROM TB_PAY_ORDER

    		1=1
    		AND applyNo = #{applyNo,jdbcType=VARCHAR}
      		AND orderNo = #{orderNo,jdbcType=VARCHAR}
     		AND STATUS =  #{status,jdbcType=VARCHAR}

		ORDER BY if(ISNULL(PAYTIME),0,1),PAYTIME,APPLYNO  DESC

mapper.delete:
		UPDATE TB_PAY_ORDER SET DELETEFLAG = '1'  WHERE APPLYNO IN (

            #{applyNo}

        )   AND (STATUS = '01' or STATUS = '05') ;
